import React from 'react';
import { motion } from 'framer-motion';
import TypewriterHeading from '../TypewriterHeading';

const IntroPanel = () => {
  return (
    <div className="flex flex-col items-center justify-center h-full text-center px-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.7 }}
        className="w-full max-w-2xl"
      >
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.3 }}
          className="mb-6"
        >
          <TypewriterHeading
            text="Hi, I'm Naina!"
            className="text-5xl md:text-6xl font-bold mb-4"
            style={{
              background: 'linear-gradient(45deg, #FFB6C1, #E6E6FA, #FFD700)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text',
            }}
            speed={120}
            delay={500}
          />
        </motion.div>

        <motion.p
          className="text-xl md:text-2xl mb-4"
          style={{ color: '#E6E6FA' }}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.6 }}
        >
          Weaving purpose, AI, and Web3 into elegant full-stack experiences.ðŸ”®
        </motion.p>

        <motion.p
          className="text-base md:text-lg mb-8"
          style={{ color: '#FDF6E3', opacity: 0.9 }}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.9 }}
        >
          A beginner full-stack dev exploring Web3 and AI, learning to build with creativity and curiosity.
        </motion.p>

        <motion.div
          className="flex gap-4 justify-center flex-wrap"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 1.2 }}
        >
          <motion.a
            href="https://docs.google.com/document/d/1ioKoaqr6cEQX3viOitwbKynTcurK5phSjvFs5_3KQ3g/edit?usp=sharing"
            target="_blank"
            rel="noopener noreferrer"
            className="px-6 py-3 rounded-full font-semibold text-base"
            style={{
              background: 'linear-gradient(45deg, #FFB6C1, #E6E6FA)',
              color: '#1a1a2e',
              textDecoration: 'none',
              boxShadow: '0 0 20px rgba(255, 182, 193, 0.3)',
            }}
            whileHover={{ scale: 1.05, y: -2 }}
            whileTap={{ scale: 0.95 }}
          >
            My Resume
          </motion.a>
          <motion.a
            href="#contact"
            className="px-6 py-3 rounded-full font-semibold text-base border-2"
            style={{
              background: 'transparent',
              color: '#FDF6E3',
              borderColor: '#FFB6C1',
              textDecoration: 'none',
            }}
            whileHover={{
              background: '#FFB6C1',
              color: '#1a1a2e',
              scale: 1.05,
              y: -2,
            }}
            whileTap={{ scale: 0.95 }}
          >
            Get In Touch
          </motion.a>
        </motion.div>
      </motion.div>
    </div>
  );
};

export default IntroPanel;
