import React from 'react';
import { motion } from 'framer-motion';

const skillCategories = [
  {
    title: "Frontend",
    skills: [
      { name: "HTML", icon: "fab fa-html5" },
      { name: "CSS", icon: "fab fa-css3-alt" },
      { name: "JavaScript", icon: "fab fa-js" },
      { name: "React", icon: "fab fa-react" },
      { name: "React Native", icon: "fab fa-react" },
    ]
  },
  {
    title: "Backend",
    skills: [
      { name: "Node.js", icon: "fab fa-node-js" },
      { name: "Express.js", icon: "fas fa-server" },
      { name: "SQL", icon: "fas fa-database" },
    ]
  },
  {
    title: "AI/ML",
    skills: [
      { name: "Python", icon: "fab fa-python" },
      { name: "GenAI", icon: "fas fa-brain" },
      { name: "LangChain", icon: "fas fa-link" },
    ]
  },
  {
    title: "Web3",
    skills: [
      { name: "Solidity", icon: "fas fa-cube" },
      { name: "Hardhat", icon: "fas fa-hat-wizard" },
      { name: "Ethereum basics", icon: "fab fa-ethereum" },
      { name: "Scaffold-eth", icon: "fas fa-tools" },
    ]
  },
  {
    title: "Tools",
    skills: [
      { name: "Git", icon: "fab fa-git-alt" },
      { name: "GitHub", icon: "fab fa-github" },
      { name: "VS Code", icon: "fas fa-code" },
      { name: "Postman", icon: "fas fa-envelope-open-text" },
      { name: "Figma", icon: "fab fa-figma" },
      { name: "Android Studio", icon: "fab fa-android" },
    ]
  }
];

const SkillsPanel = () => {
  return (
    <div className="w-full h-full overflow-y-auto px-4 py-6">
      <motion.h2
        className="text-3xl md:text-4xl font-bold text-center mb-8"
        style={{
          background: 'linear-gradient(45deg, #FFB6C1, #E6E6FA)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
        }}
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        Skills
      </motion.h2>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 max-w-5xl mx-auto">
        {skillCategories.map((category, categoryIndex) => (
          <motion.div
            key={categoryIndex}
            className="rounded-xl p-4"
            style={{
              background: 'rgba(255, 255, 255, 0.05)',
              border: '1px solid rgba(255, 182, 193, 0.2)',
              backdropFilter: 'blur(10px)',
            }}
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: categoryIndex * 0.1 }}
            whileHover={{
              scale: 1.02,
              boxShadow: '0 10px 25px rgba(255, 182, 193, 0.3)',
            }}
          >
            <h3
              className="text-xl font-bold mb-3 text-center"
              style={{ color: '#FFB6C1' }}
            >
              {category.title}
            </h3>
            <div className="grid grid-cols-2 gap-2">
              {category.skills.map((skill, skillIndex) => (
                <motion.div
                  key={skillIndex}
                  className="flex flex-col items-center p-2 rounded"
                  style={{
                    background: 'rgba(255, 255, 255, 0.03)',
                  }}
                  whileHover={{
                    scale: 1.05,
                    backgroundColor: 'rgba(255, 182, 193, 0.1)',
                  }}
                  transition={{ duration: 0.2 }}
                >
                  {skill.icon && (
                    <i
                      className={skill.icon}
                      style={{
                        fontSize: '1.2rem',
                        color: '#FFD700',
                        marginBottom: '0.2rem',
                      }}
                    />
                  )}
                  <span
                    className="text-xs font-medium"
                    style={{ color: '#FDF6E3' }}
                  >
                    {skill.name}
                  </span>
                </motion.div>
              ))}
            </div>
          </motion.div>
        ))}
      </div>
    </div>
  );
};

export default SkillsPanel;
