import React, { useState, useRef, useEffect } from 'react';
import { motion, useMotionValue, useSpring, useDragControls, useTransform } from 'framer-motion';
import IntroPanel from './panels/IntroPanel';
import AboutPanel from './panels/AboutPanel';
import SkillsPanel from './panels/SkillsPanel';
import ProjectsPanel from './panels/ProjectsPanel';
import ContactPanel from './panels/ContactPanel';

// Panel Wrapper Component to handle transforms
const PanelWrapper = ({ index, baseAngle, angleStep, springRotation, getPanelTransform, activeIndex, children }) => {
  const panelOpacity = useTransform(springRotation, (rot) => {
    const panelAngle = ((baseAngle + rot) % 360 + 360) % 360;
    const distanceFromCenter = Math.min(panelAngle, 360 - panelAngle);
    const maxDistance = angleStep / 2;
    return Math.max(0.2, 1 - (distanceFromCenter / maxDistance) * 0.8);
  });

  const panelScale = useTransform(springRotation, (rot) => {
    const panelAngle = ((baseAngle + rot) % 360 + 360) % 360;
    const distanceFromCenter = Math.min(panelAngle, 360 - panelAngle);
    const maxDistance = angleStep / 2;
    return Math.max(0.85, 1 - (distanceFromCenter / maxDistance) * 0.15);
  });

  const panelBrightness = useTransform(springRotation, (rot) => {
    const panelAngle = ((baseAngle + rot) % 360 + 360) % 360;
    const distanceFromCenter = Math.min(panelAngle, 360 - panelAngle);
    const maxDistance = angleStep / 2;
    return Math.max(0.6, 1 - (distanceFromCenter / maxDistance) * 0.4);
  });

  const panelFilter = useTransform(panelBrightness, (b) => `brightness(${b})`);

  return (
    <motion.div
      className="absolute w-full max-w-4xl h-[90vh] flex items-center justify-center"
      style={{
        transform: getPanelTransform(index),
        transformStyle: 'preserve-3d',
        backfaceVisibility: 'hidden',
        WebkitBackfaceVisibility: 'hidden',
        pointerEvents: index === activeIndex ? 'auto' : 'none',
        opacity: panelOpacity,
        scale: panelScale,
        filter: panelFilter,
      }}
    >
      <div 
        className="w-full h-full overflow-y-auto"
        style={{
          background: 'rgba(255, 255, 255, 0.03)',
          backdropFilter: 'blur(10px)',
          borderRadius: '20px',
          border: '1px solid rgba(255, 182, 193, 0.2)',
          boxShadow: '0 8px 32px rgba(138, 43, 226, 0.1)',
          padding: '2rem',
        }}
      >
        {children}
      </div>
    </motion.div>
  );
};

const CylinderPortfolio = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const dragControls = useDragControls();
  const containerRef = useRef(null);

  const numPanels = 5;
  const angleStep = 360 / numPanels; // 72 degrees per panel
  const radius = 800; // Distance from center to panel (cylinder radius)

  // Motion value for rotation
  const rotation = useMotionValue(0);
  const springRotation = useSpring(rotation, {
    stiffness: 100,
    damping: 30,
    mass: 0.5
  });

  // Calculate panel positions and styles based on current rotation
  const getPanelTransform = (index) => {
    const baseAngle = index * angleStep;
    return `rotateY(${baseAngle}deg) translateZ(${radius}px)`;
  };

  // Snap to nearest panel
  const snapToNearest = (currentRotation) => {
    const normalizedRotation = ((currentRotation % 360) + 360) % 360;
    // Find the closest panel index
    let minDistance = Infinity;
    let targetIndex = 0;

    for (let i = 0; i < numPanels; i++) {
      const panelAngle = i * angleStep;
      const distance = Math.min(
        Math.abs(normalizedRotation - panelAngle),
        Math.abs(normalizedRotation - (panelAngle + 360)),
        Math.abs(normalizedRotation - (panelAngle - 360))
      );
      if (distance < minDistance) {
        minDistance = distance;
        targetIndex = i;
      }
    }

    const targetRotation = -(targetIndex * angleStep);
    rotation.set(targetRotation);
    setActiveIndex(targetIndex);
  };

  // Handle drag
  const handleDrag = (event, info) => {
    const deltaX = info.delta.x;
    const sensitivity = 0.8; // Adjust sensitivity
    const rotationDelta = (deltaX / window.innerWidth) * 360 * sensitivity;

    const currentRotation = rotation.get();
    const newRotation = currentRotation - rotationDelta;
    rotation.set(newRotation);

    // Update active index during drag
    const normalizedRotation = ((newRotation % 360) + 360) % 360;
    let minDistance = Infinity;
    let closestIndex = 0;

    for (let i = 0; i < numPanels; i++) {
      const panelAngle = i * angleStep;
      const distance = Math.min(
        Math.abs(normalizedRotation - panelAngle),
        Math.abs(normalizedRotation - (panelAngle + 360)),
        Math.abs(normalizedRotation - (panelAngle - 360))
      );
      if (distance < minDistance) {
        minDistance = distance;
        closestIndex = i;
      }
    }
    setActiveIndex(closestIndex);
  };

  const handleDragStart = () => {
    setIsDragging(true);
  };

  const handleDragEnd = (event, info) => {
    setIsDragging(false);

    // Calculate velocity-based rotation
    const velocity = info.velocity.x;
    const velocityRotation = (velocity / window.innerWidth) * 360 * 0.3;

    const currentRotation = rotation.get();
    const finalRotation = currentRotation - velocityRotation;

    // Snap to nearest panel
    snapToNearest(finalRotation);
  };

  // Keyboard navigation
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.key === 'ArrowLeft') {
        const newIndex = (activeIndex - 1 + numPanels) % numPanels;
        setActiveIndex(newIndex);
        rotation.set(-(newIndex * angleStep));
      } else if (e.key === 'ArrowRight') {
        const newIndex = (activeIndex + 1) % numPanels;
        setActiveIndex(newIndex);
        rotation.set(-(newIndex * angleStep));
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [activeIndex, angleStep, rotation]);

  const panels = [
    { component: IntroPanel, id: 'intro' },
    { component: AboutPanel, id: 'about' },
    { component: SkillsPanel, id: 'skills' },
    { component: ProjectsPanel, id: 'projects' },
    { component: ContactPanel, id: 'contact' },
  ];

  return (
    <div 
      className="relative w-full h-screen overflow-hidden"
      style={{
        background: 'linear-gradient(135deg, #1a1a2e, #16213e)',
        perspective: '1000px',
      }}
    >
      {/* Background effects */}
      <div 
        className="absolute inset-0 pointer-events-none"
        style={{
          backgroundImage: `
            radial-gradient(circle at 20% 80%, rgba(255, 182, 193, 0.1) 0%, transparent 50%),
            radial-gradient(circle at 80% 20%, rgba(230, 230, 250, 0.1) 0%, transparent 50%),
            radial-gradient(circle at 40% 40%, rgba(255, 214, 0, 0.05) 0%, transparent 50%)
          `,
          zIndex: 0,
        }}
      />

      {/* 3D Container */}
      <div
        ref={containerRef}
        className="absolute inset-0 flex items-center justify-center"
        style={{
          transformStyle: 'preserve-3d',
          perspective: '1000px',
        }}
      >
        <motion.div
          className="absolute inset-0 flex items-center justify-center"
          style={{
            transformStyle: 'preserve-3d',
            rotateY: springRotation,
          }}
          drag="x"
          dragControls={dragControls}
          dragConstraints={{ left: 0, right: 0 }}
          dragElastic={0.1}
          onDrag={handleDrag}
          onDragStart={handleDragStart}
          onDragEnd={handleDragEnd}
        >
          {/* Panels */}
          {panels.map((panel, index) => {
            const PanelComponent = panel.component;
            const baseAngle = index * angleStep;

            return (
              <PanelWrapper
                key={panel.id}
                index={index}
                baseAngle={baseAngle}
                angleStep={angleStep}
                springRotation={springRotation}
                getPanelTransform={getPanelTransform}
                activeIndex={activeIndex}
              >
                <PanelComponent />
              </PanelWrapper>
            );
          })}
        </motion.div>
      </div>

      {/* Navigation Dots */}
      <div 
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 flex gap-3 z-50"
        style={{
          background: 'rgba(40, 40, 60, 0.55)',
          backdropFilter: 'blur(16px)',
          padding: '0.75rem 1.5rem',
          borderRadius: '2rem',
          border: '1.5px solid rgba(255, 255, 255, 0.08)',
        }}
      >
        {panels.map((_, index) => (
          <button
            key={index}
            onClick={() => {
              setActiveIndex(index);
              rotation.set(-(index * angleStep));
            }}
            className={`w-3 h-3 rounded-full transition-all duration-300 ${
              index === activeIndex 
                ? 'bg-gradient-to-r from-pink-300 to-purple-300 scale-125' 
                : 'bg-white/30 hover:bg-white/50'
            }`}
            aria-label={`Go to panel ${index + 1}`}
          />
        ))}
      </div>

      {/* Instructions */}
      <div 
        className="absolute top-8 left-1/2 transform -translate-x-1/2 text-center z-50"
        style={{
          color: 'rgba(230, 230, 250, 0.7)',
          fontSize: '0.875rem',
          pointerEvents: 'none',
        }}
      >
        <p>Drag horizontally or use arrow keys to navigate</p>
      </div>
    </div>
  );
};

export default CylinderPortfolio;
