import React, { useEffect, useState } from 'react';

const BrewingMagicLanding = ({ onFinish }) => {
  const [displayedText, setDisplayedText] = useState('');
  const [showSwish, setShowSwish] = useState(false);
  const [fadeText, setFadeText] = useState(false);
  const fullText = 'Brewing Magic...';

  useEffect(() => {
    let i = 0;
    const typeInterval = setInterval(() => {
      setDisplayedText(fullText.slice(0, i + 1));
      i++;
      if (i === fullText.length) {
        clearInterval(typeInterval);
        // Fade out text first
        setTimeout(() => setFadeText(true), 400);
        // Then start swish after fade begins
        setTimeout(() => setShowSwish(true), 600);
      }
    }, 90);
    return () => clearInterval(typeInterval);
  }, []);

  // When swish animation ends, trigger onFinish
  const handleSwishEnd = () => {
    if (onFinish) onFinish();
  };

  return (
    <div
      style={{
        minHeight: '100vh',
        background: '#1a1a1a',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
        overflow: 'hidden',
        width: '100vw',
        boxSizing: 'border-box',
        padding: '0 1rem',
      }}
    >
      {/* Text with fade-out animation */}
      {!showSwish && (
        <h1
          style={{
            fontFamily: "'Cormorant Garamond', serif",
            fontSize: '2.5rem',
            color: '#f5f5f5',
            textShadow: '0 0 4px rgba(201, 154, 154, 0.3), 0 0 8px rgba(201, 154, 154, 0.2)',
            marginBottom: '1.5rem',
            letterSpacing: '0.05em',
            zIndex: 2,
            fontWeight: 700,
            whiteSpace: 'pre',
            opacity: fadeText ? 0 : 1,
            transform: fadeText ? 'translateY(-20px)' : 'translateY(0)',
            transition: 'opacity 0.4s ease-out, transform 0.4s ease-out',
          }}
        >
          {displayedText}
          <span style={{
            display: 'inline-block',
            width: '1ch',
            background: 'none',
            color: '#c99a9a',
            animation: fadeText ? 'none' : 'blink 1s steps(1) infinite',
          }}>|</span>
        </h1>
      )}
      {/* Swish Animation Overlay */}
      {showSwish && (
        <div
          className="swish-overlay"
          onAnimationEnd={handleSwishEnd}
          style={{
            position: 'fixed',
            top: 0, left: 0, width: '100vw', height: '100vh',
            zIndex: 999,
            pointerEvents: 'none',
            animation: 'swish-magic 0.9s cubic-bezier(0.7,0,0.3,1) forwards',
            background: 'linear-gradient(120deg, #c99a9a 0%, #d4a5a5 100%)',
            boxShadow: '0 0 80px 40px rgba(201, 154, 154, 0.3)',
            opacity: 0.95,
          }}
        />
      )}
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Cormorant+Garamond:wght@400;500;600;700&display=swap');
        @keyframes blink {
          0%, 100% { opacity: 1; }
          50% { opacity: 0; }
        }
        @keyframes swish-magic {
          0% {
            transform: translateX(-100vw) skewX(-20deg);
            opacity: 0.7;
          }
          60% {
            opacity: 1;
          }
          100% {
            transform: translateX(0vw) skewX(0deg);
            opacity: 0;
          }
        }
        @media (max-width: 700px) {
          h1 {
            font-size: 1.8rem !important;
          }
        }
      `}</style>
    </div>
  );
};

export default BrewingMagicLanding;
