import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <section id="home" className="hero">
      <div className="container">
        <div className="hero-grid">
          <motion.div 
            className="hero-block large"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7 }}
          >
            <span className="hashtag">#portfolio #developer #web3</span>
            <h1 className="hero-title">naina sachdev</h1>
            <p className="hero-subtitle">
              Weaving purpose, AI, and Web3 into elegant full-stack experiences.
            </p>
            <p className="hero-description">
              A beginner full-stack dev exploring Web3 and AI, learning to build with creativity and curiosity. 
              Currently working on projects that bridge the gap between traditional web development and decentralized technologies.
            </p>
            <div style={{ marginTop: '1.5rem', display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
              <a 
                href="https://docs.google.com/document/d/1ioKoaqr6cEQX3viOitwbKynTcurK5phSjvFs5_3KQ3g/edit?usp=sharing"
                className="btn"
                target="_blank"
                rel="noopener noreferrer"
              >
                resume
              </a>
              <a 
                href="#contact" 
                className="btn"
                onClick={(e) => { e.preventDefault(); scrollToSection('contact'); }}
              >
                contact
              </a>
            </div>
          </motion.div>

          <motion.div 
            className="hero-block"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: 0.2 }}
          >
            <span className="hashtag">#about</span>
            <h2 className="content-block-title">basic info</h2>
            <p className="content-block-text">
              AI and Data Science engineering student exploring how Web3, AI, and creativity can 
              come together to build useful, meaningful things.
            </p>
            <p className="decorative-text" style={{ marginTop: '1rem' }}>
              building the future, one commit at a time
            </p>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
